    private void jButtonSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSalvarActionPerformed
        String nacionalidade = (String) jComboBoxNacionalidade.getSelectedItem();
        AtorController atorController = new AtorController();

        if ((jTextFieldNome.getText() != null) && (nacionalidade != null)) {
            if (jComboBoxNacionalidade.getSelectedIndex() == 0) {
                JOptionPane.showMessageDialog(null, "Escolha uma nacinalidade!");
                return;
            }

            atorController.save(jTextFieldNome.getText(), nacionalidade);
            jTextFieldNome.setText("");
            jComboBoxNacionalidade.setSelectedIndex(0);
        }
    }//GEN-LAST:event_jButtonSalvarActionPerformed

    private void jButtonLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonLimparActionPerformed
        jTextFieldNome.setText("");
        jComboBoxNacionalidade.setSelectedIndex(0);
    }//GEN-LAST:event_jButtonLimparActionPerformed

